mapper.select:
select
,
from task where task_id = #{taskId,jdbcType=INTEGER}

select

from task
where pack_id = #{packId} order by task_id DESC limit #{begin},#{end}

select

from task
where pack_id = #{packId} and (task_mark_time Is NULL or task_mark_time =0) order by task_id DESC limit #{begin},#{end}

select

from task
where pack_id = #{packId} and task_mark_time > 0 order by task_id DESC limit #{begin},#{end}

select

,

from task
where worker_id IS NULL
and pack_id IN (select pack_id from pack where pack_status=0 and unzip = 1

	and pack_type = ${packType}

order by pack_lvl
)
order by task_id
limit #{downTaskCount,jdbcType=INTEGER}

select

,

from task

	worker_id = ${workerId}
	and
	task_upload = ${taskUpload}

select count(task_id) total from task where worker_id IS NULL AND pack_id IN (SELECT pack_id FROM pack WHERE pack_status=0 AND unzip = 1
AND pack_type = ${packType}
)

select
,
from task WHERE task_name = #{taskName,jdbcType=VARCHAR}

mapper.update:
update task

	worker_id = null

where 
	worker_id = ${workerId}
	and
	task_id = ${taskId}

update task

	worker_id = null,task_upload = 0

where 
	worker_id = ${workerId}
	and
	task_upload = ${taskUpload}
